package whileanddowhile;

public class exercise2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,suma;
		suma = 0;
		i = 1;
		while(i<=100) {
			
			if (i%2==0) {
				int suma = i+suma;
				System.out.print(suma);
			}
			i++;
			
		}
	
		
		
	
	}
		
		
		
	}
	
	

